mapper.select:
UPDATE tbl_order
SET 
orderCount = #{orderCount}
WHERE ono = #{ono}

SELECT o.ono, o.email, o.registDate, s.sno, s.storeName, s.storePhone, o.orderCount, h.orderStatus, m.store, p.productName, p.productPrice, CONCAT(pi.uuid, '_', pi.fileName) as fileName FROM tbl_order o LEFT OUTER JOIN tbl_order_history h ON o.ono = h.ono LEFT OUTER JOIN tbl_product p ON o.pno = p.pno LEFT OUTER JOIN tbl_product_image pi ON pi.pno = p.pno AND pi.ord = 0 LEFT OUTER JOIN tbl_store s ON o.sno = s.sno LEFT OUTER JOIN tbl_member m on m.email = o.email WHERE o.ono > 0 AND h.ono > 0
o.email like concat('%', #{keyword}, '%')
o.ono like concat('%', #{keyword}, '%')
s.storeName like concat('%', #{keyword}, '%')
AND o.registDate >= #{startDate}
OR o.registDate <= #{endDate}
ORDER BY o.ono DESC, h.orderHistory DESC LIMIT #{skip}, #{size}

SELECT o.ono, o.email, o.registDate, s.sno, s.storeName, s.storePhone, o.orderCount, h.orderStatus, m.store, p.productName, p.productPrice, CONCAT(pi.uuid, '_', pi.fileName) as fileName FROM tbl_order o LEFT OUTER JOIN tbl_order_history h ON o.ono = h.ono LEFT OUTER JOIN tbl_product p ON o.pno = p.pno LEFT OUTER JOIN tbl_product_image pi ON pi.pno = p.pno AND pi.ord = 0 LEFT OUTER JOIN tbl_store s ON o.sno = s.sno LEFT OUTER JOIN tbl_member m on m.email = o.email WHERE o.ono > 0 AND h.ono > 0 AND s.sno = #{sno}
o.email like concat('%', #{pr.keyword}, '%')
o.ono like concat('%', #{pr.keyword}, '%')
s.storeName like concat('%', #{pr.keyword}, '%')
AND o.registDate >= #{pr.startDate}
AND o.registDate <= #{pr.endDate}
ORDER BY o.ono DESC, h.orderHistory DESC LIMIT #{pr.skip}, #{pr.size}

SELECT COUNT(*)
FROM(
    SELECT 
    o.ono,
    s.sno
    FROM tbl_order             o
    LEFT OUTER JOIN tbl_store  s ON o.sno = s.sno
    LEFT OUTER JOIN tbl_member m ON s.email = m.email
    WHERE o.ono > 0 AND s.sno = #{sno}




            o.email like concat('%', #{pr.keyword}, '%')


            o.ono like concat('%', #{pr.keyword}, '%')


            s.storeName like concat('%', #{pr.keyword}, '%')





        AND o.registDate >= #{pr.startDate}


        AND o.registDate <= #{pr.endDate}

LIMIT #{pr.countEnd}
) as tbl_order
